import React from 'react'
import TagList from 'components/taglist/taglist'

import s from './Feed.module.css'
import { Link } from 'react-router-dom'

const Feed = ({articles}) => {

    return(
        <div className={s.container}>
            {articles.map((article,index)=>(
                <div className={s.info} key={index}>
                    <div className={s.img}> 
                    <Link to={`/profile/${article.author.username}`}>
                        <img src={article.author.image} className={s.avatar} alt=" " />
                    </Link>
                    <div className={s.name}> 
                    <Link className={s.link1}  to={`/profile/${article.author.username}`}>
                        {article.author.username}
                    </Link>
                        <span>{article.createdAt}</span>
                    </div>
                    </div>
                <div className={s.titel}>  
                    <Link className={s.link2} to={`/articles/${article.slug}`} >
            <h1 className={s.titels}>{article.title}</h1>
            <p>{article.description}</p>
            <span>Read more....</span>
            
                    <TagList tags={article.tagList} />
                    </Link>
                </div>
                </div> ))}
        </div>
    )
}

export default Feed