import React, { useContext } from 'react'
import { NavLink } from 'react-router-dom'
import s from './FeedToggler.module.css'
import {CurrentUserContext} from 'contexts/currentUser'

const FeedToggler = ({tagName}) => {
    const [currentUserState]= useContext(CurrentUserContext)
    return (
        <div className={s.container}>
            <ul className={s.ul}>
               {currentUserState.isLoggedIn && (
                 <li className={s.li}>
                    <NavLink to='/feed' className={s.link}>Your Feed </NavLink>
                </li>
                )}
               
                <li className={s.li}>
                    <NavLink to='/' className={s.link } excat>Global Feed </NavLink>
                </li>
                <li className={s.li}>
    {tagName && ( <NavLink to={`/tags/${tagName}`} className={s.link}>#{tagName}</NavLink>)}
                   
                </li>
            </ul>
        </div>
    )
}
export default FeedToggler