import React from 'react'

import { Switch, Route } from 'react-router-dom'

import GlobalFeed from 'pages/globalfeed'
import Article from 'pages/article'
import Autenfication from 'pages/autenfication/autenfication'
import TegFeed from 'pages/tegFed/index'
import YourFeed from 'pages/yourfeed/YourFeed'
import CreatArticle from 'pages/creatArticle/creatArticle'
import EditArticle from 'pages/EditArticle/EditArticle'
import Settings from 'pages/Settings/Settigs'
import UserProfile from 'pages/UserProfile/UserProfile'
 
export default  () => (
    <Switch>
        <Route path='/'  component={GlobalFeed} exact />
        <Route path='/profiles/:slug'  component={UserProfile}  />
        <Route path='/profiles/:slug/favorites'  component={UserProfile}  />
        <Route path='/settings'  component={Settings} />
        <Route path='/articles/new'  component={CreatArticle}  />
        <Route path='/articles/:slug/edit'  component={EditArticle}  />
        <Route path='/feed'  component={YourFeed}  />
        <Route path='/tags/:slug'  component={TegFeed}  />
        <Route path='/login' component={Autenfication} />
        <Route path='/register' component={Autenfication} />
        <Route path='/articles/:slug'  component={Article } />
    </Switch>
)
